import "core-js/modules/es.array.push.js";
import BasketList from '../../src/components/BasketList.vue';
import { mapGetters, mapMutations } from 'vuex';
export default {
  components: {
    BasketList
  },
  computed: {
    ...mapGetters('shop', ['cartItems'])
  },
  methods: {
    ...mapMutations('shop', ['clearCart']),
    processPayment() {
      // Logique pour effectuer le paiement
      // Vous pouvez appeler une action dans le store pour effectuer les opérations nécessaires
      // N'oubliez pas de vider le panier après le paiement en appelant la mutation clearCart

      // Générez un UUID v4 pour idcmd (vous devez installer la bibliothèque uuid, si ce n'est pas déjà fait)
      // npm install uuid
      const idcmd = require('uuid').v4();

      // Ajoutez l'ID de la commande à l'URL et redirigez
      this.$router.push({
        name: 'shoppay',
        params: {
          idcmd
        }
      });
      this.clearCart();
    }
  }
};
