import "core-js/modules/es.array.push.js";
import NavBar from "@/components/NavBar";
import { mapState } from "vuex";
export default {
  name: "App",
  components: {
    NavBar
  },
  computed: {
    ...mapState("shop", ["shopUser"])
  },
  data: () => ({
    titles: [],
    currentIndex: -1
  }),
  watch: {
    shopUser: {
      handler: "updateNavBar",
      immediate: true
    }
  },
  methods: {
    goTo(index) {
      if (this.$store.state.shopUser !== undefined) {
        if (index === 0 && this.$route.name !== '/shop/items') {
          this.$router.push('/shop/items');
        } else if (index === 1 && this.$route.name !== '/bank/account') {
          this.$router.push('/bank/account');
        } else if (index === 2 && this.$route.name !== 'shoplogin') {
          this.$router.push({
            name: 'shoplogin'
          });
        }
      } else {
        if (index === 0 && this.$route.name !== 'shoplogin') {
          this.$router.push({
            name: 'shoplogin'
          });
        }
      }
    },
    updateNavBar() {
      if (this.shopUser !== null && this.shopUser !== undefined) {
        console.log("User is logged in. Update the navbar here.");
        this.titles = [{
          text: "Virus",
          color: "blue"
        }, {
          text: "Compte bancaire",
          color: "red"
        }, {
          text: "Login",
          color: "green"
        }];
      } else {
        console.log(this.shopUser);
        this.titles = [{
          text: "Login",
          color: "green"
        }];
      }
    }
  }
};
