import "core-js/modules/es.array.push.js";
import NavBar from "@/components/NavBar";
export default {
  name: 'App',
  components: {
    NavBar
  },
  data: () => ({
    titles: [],
    currentIndex: -1
  }),
  mounted() {
    if (this.$store.state.shopUser !== null) {
      this.titles = [{
        text: 'Login',
        color: 'green'
      }];
    } else {
      this.titles = [{
        text: 'Virus',
        color: 'blue'
      }, {
        text: 'Compte bancaire',
        color: 'red'
      }, {
        text: 'Login',
        color: 'green'
      }];
    }
  },
  methods: {
    goTo(index) {
      if (this.$store.state.shopUser !== null) {
        this.$router.push({
          name: 'shoplogin'
        });
      } else {
        if (index === 0) {
          this.$router.push('/shop/items');
        } else if (index === 1) {
          this.$router.push('/bank/account');
        } else if (index === 2) {
          this.$router.push({
            name: 'shoplogin'
          });
        }
      }
    }
  }
};
