import ShopService from '../services/shop.service'
import router from "@/router";

export default {
    state: () => ({
        basket: [],
        shopUser: null,
        viruses: []
    }),

    mutations: {
        updateViruses(state, viruses) {
            state.viruses = viruses
        },

        updateShopUser(state, user) {
            state.shopUser = user;
        },

        addToBasket(state, { virus, quantity }) {
            state.basket.push({ virus, quantity });
        },

        updateBasketItemQuantity(state, { index, quantity }) {
            state.basket[index].quantity += quantity;
        },
    },

    actions: {
        async shopLogin({ commit }, data) {
            let response = await ShopService.shopLogin(data)
            if (response.error === 0) {
                commit('updateShopUser', response.data);
                this.state.shopUser = response.data;
                console.log("Connection réussi !");
                await router.push('/shop');
            } else {
                console.log(response.data)
            }
        },

        async shopLogout() {
            this.state.shopUser = null;
            console.log("Déconnections réussi !");
            await router.push('/');
        },

        async getAllViruses({commit}) {
            console.log('Récupération des viruses');
            let response = await ShopService.getAllViruses()
            if (response.error === 0) {
                commit('updateViruses', response.data)
                this.state.viruses = response.data;
                console.log("Récupération des viruses réussi !");
            } else {
                console.log(response.data)
            }
        },

        addToCartAction({ commit, state }, { virus, quantity }) {
            // Vérifie si le virus est déjà dans le panier
            const existingItemIndex = state.basket.findIndex(item => item.virus._id === virus._id);

            if (existingItemIndex !== -1) {
                // Si le virus existe, met à jour la quantité dans le panier du store
                commit('updateBasketItemQuantity', { index: existingItemIndex, quantity });
            } else {
                // Sinon, ajoute le virus avec la quantité spécifiée dans le panier du store
                commit('addToBasket', { virus, quantity });
            }
        },

        removeFromCartAction({ commit }, selectedCartItem) {
            commit('removeFromCartMutation', selectedCartItem);
        },
    }
}
