import ShopService from '../services/shop.service'
import router from "@/router";

export default {
    state: () => ({
        basket: [],
        shopUser: null,
        viruses: [],
        orders: []
    }),

    mutations: {
        updateShopUser(state, user) {
            state.shopUser = user;
        },
    },

    actions: {
        async shopLogin({ commit }, data) {
            let response = await ShopService.shopLogin(data)
            if (response.error === 0) {
                commit('updateShopUser', response.data);
                this.state.shopUser = response.data;
                console.log("Connection réussi !");
                await router.push('/shop');
            } else {
                console.log(response.data)
            }
        },

        async shopLogout() {
            this.state.shopUser = null;
            console.log("Déconnexion réussie !");
            await router.push('/');
        },

        async getAllViruses() {
            let response = await ShopService.getAllViruses();
            if (response.error === 0) {
                this.state.viruses = response.data;
                console.log("Récupération des virus réussie !");
            } else {
                console.log(response.data);
            }
        },

        addToCartAction({ state }, { virus, quantity }) {
            // Vérifie si le virus est déjà dans le panier
            const existingItemIndex = state.basket.findIndex(item => item.virus._id === virus._id);

            if (existingItemIndex !== -1) {
                // Si le virus existe, met à jour la quantité dans le panier du store
                state.basket[existingItemIndex].quantity += quantity;
            } else {
                // Sinon, ajoute le virus avec la quantité spécifiée dans le panier du store
                state.basket.push({ virus, quantity });
            }
        },

        removeFromCartAction({ state }, selectedCartItem) {
            // Recherche de l'index de l'élément dans le panier
            const index = state.basket.findIndex(item => item.virus._id === selectedCartItem.virus._id);

            if (index !== -1) {
                // Si l'élément est trouvé, le retire du panier
                state.basket.splice(index, 1);
            }
        },

        clearCartAction() {
            // Vide le panier directement
            this.state.basket = [];
        },

        checkoutAction({ state }, cartItems) {
            // Logique pour finaliser l'achat
            console.log('Finalisation de l\'achat avec les articles:', cartItems);

            // Vous pouvez ajouter ici la logique spécifique pour finaliser l'achat
            // (envoi des données au backend, traitement des paiements, etc.)

            // Simulation de la création d'une commande
            const newOrder = {
                _id: 'new_order_id', // Remplacez par l'ID réel de la commande généré par le backend
                items: cartItems.map(item => ({ item, amount: item.quantity })),
                date: new Date(),
                total: cartItems.reduce((total, item) => total + item.virus.price * item.quantity, 0),
                status: 'finalized',
                uuid: 'new_order_uuid', // Remplacez par l'UUID réel généré par le backend
            };

            // Mise à jour du store avec la nouvelle commande
            this.state.orders.push(newOrder);

            // Mise à jour de l'utilisateur pour ajouter la commande
            const currentUser = state.shopUser;
            if (currentUser) {
                currentUser.orders.push(newOrder._id);
            }
        },
    }
}
