export default {
  components: {
    CheckedList
  },
  data() {
    return {
      viruses: [/* Votre liste de virus */],
      checkedViruses: Array(this.viruses.length).fill(false)
    };
  },
  methods: {
    toggleVirusCheckbox(index) {
      this.$set(this.checkedViruses, index, !this.checkedViruses[index]);
    },
    addToCart({
      index,
      amount
    }) {
      // Logique pour ajouter un virus au panier de l'utilisateur courant (dans shopUser)
      const selectedVirus = this.viruses[index];
      // Ajouter la logique pour mettre à jour le panier dans shopUser avec le virus sélectionné et la quantité
      console.log(`Ajouter au panier : ${selectedVirus.name}, Quantité : ${amount}`);
    },
    addAllToCart(selectedItems) {
      // Logique pour ajouter tous les virus sélectionnés au panier de l'utilisateur courant (dans shopUser)
      selectedItems.forEach(({
        index,
        amount
      }) => {
        const selectedVirus = this.viruses[index];
        // Ajouter la logique pour mettre à jour le panier dans shopUser avec le virus sélectionné et la quantité
        console.log(`Ajouter au panier : ${selectedVirus.name}, Quantité : ${amount}`);
      });
    },
    updateAmount({
      index,
      amount
    }) {
      // Logique pour mettre à jour la quantité d'un virus dans la liste
      console.log(`Mise à jour de la quantité de ${this.viruses[index].name} : ${amount}`);
    }
  }
};
