import "core-js/modules/es.array.push.js";
import NavBar from "@/components/NavBar";
import { mapActions } from "vuex";
export default {
  name: "App",
  components: {
    NavBar
  },
  data: () => ({
    titles: [],
    currentIndex: -1
  }),
  mounted() {
    this.updateNavBar();
  },
  watch: {
    $route: "updateNavBar"
  },
  methods: {
    goTo(index) {
      if (this.titles.length > 1) {
        if (index === 0 && this.$route.name !== 'shophome') {
          this.$router.push('shophome');
        } else if (index === 1 && this.$route.name !== 'shopbuy') {
          this.$router.push('/shop/buy');
        } else if (index === 2 && this.$route.name !== 'shoporders') {
          this.$router.push('/shop/orders');
        } else if (index === 3 && this.$route.name !== 'shoplogout') {
          this.shopLogout();
        }
      } else if (index === 0 && this.$route.name !== 'shoplogin') {
        this.$router.push({
          name: 'shoplogin'
        });
      }
    },
    updateNavBar() {
      if (this.$store.state.shopUser !== null && this.$store.state.shopUser !== undefined) {
        this.titles = [{
          text: 'Acheter',
          color: 'blue'
        }, {
          text: 'Payer',
          color: 'red'
        }, {
          text: 'Mes commandes',
          color: 'pink'
        }, {
          text: 'Logout',
          color: 'green'
        }];
        console.log("La NavBar à été mise à jours !");
      } else {
        this.titles = [{
          text: 'Login',
          color: 'green'
        }];
        console.log("La NavBar à été mise à jours !");
      }
    },
    updateNavBarOnRouteChange() {
      this.updateNavBar();
    },
    ...mapActions(['shopLogout'])
  }
};
