// App.vue

<template>
  <div id="app">
    <NavBar :titles="titles" @menu-clicked="goTo($event)"></NavBar>

    <h1>Welcome to DrMad app</h1>
    <h1>ICI {{ this.$store.state.shopUser }}</h1>

    <router-view />
  </div>
</template>

<script>
import NavBar from "@/components/NavBar";
import { mapState } from "vuex";

export default {
  name: "App",
  components: { NavBar },
  computed: {
    ...mapState("shop", ["shopUser"]),
  },
  data: () => ({
    titles: [],
    currentIndex: -1,
  }),

  watch: {
    shopUser: {
      handler: "updateNavBar",
      immediate: true,
    },
  },

  mounted() {
    this.updateNavBar();
  },

  methods: {
    goTo(index) {
      if (this.titles.length === 3) {
        if (index === 0 && this.$route.name !== '/shop/items') {
          this.$router.push('/shop/items');
        } else if (index === 1 && this.$route.name !== '/bank/account') {
          this.$router.push('/bank/account');
        } else if (index === 2 && this.$route.name !== 'shoplogin') {
          this.$router.push({ name: 'shoplogin' });
        }
      } else {
        if (index === 0 && this.$route.name !== 'shoplogin') {
          this.$router.push({ name: 'shoplogin' });
        }
      }
    },

    updateNavBar() {
      if (this.$store.state.shopUser !== null && this.$store.state.shopUser !== undefined) {
        console.log('User is logged in. Update the navbar here.');
        this.titles = [
          { text: 'Virus', color: 'blue' },
          { text: 'Compte bancaire', color: 'red' },
          { text: 'Login', color: 'green' }
        ];
      } else {
        this.titles = [{ text: 'Login', color: 'green' }];
      }
    },
  },
};
</script>
