import { ref, computed } from 'vue';

// Prop pour recevoir le solde depuis le parent (BankView.vue)

export default {
  __name: 'BankAmount',
  props: ['balance'],
  setup(__props) {
    const props = __props;

    // Utiliser une référence pour le solde
    const balance = ref(props.balance);

    // Calculer le style en fonction du solde (rouge si négatif, vert si positif)
    const balanceStyle = computed(() => {
      return {
        color: balance.value < 0 ? 'red' : 'green'
      };
    });

    // Formater le solde avec le symbole €
    const formattedBalance = computed(() => {
      return `${balance.value} €`;
    });
    return {
      __sfc: true,
      props,
      balance,
      balanceStyle,
      formattedBalance
    };
  }
};
