import NavBar from "@/components/NavBar";
import { mapActions } from "vuex";
export default {
  name: "App",
  components: {
    NavBar
  },
  data: () => ({
    titles: [],
    currentIndex: -1
  }),
  mounted() {
    this.updateNavBar();
  },
  watch: {
    $route: "updateNavBar"
  },
  methods: {
    goTo(index) {
      if (this.titles.length > 1) {
        if (index === 0 && this.$route.name !== 'shophome') {
          this.$router.push({
            name: 'shophome'
          });
        } else if (index === 1 && this.$route.name !== 'shopbuy') {
          this.$router.push({
            name: 'shopbuy'
          });
        } else if (index === 2 && this.$route.name !== 'shoppay') {
          const idcmd = "0";
          this.$router.push({
            name: 'shoppay',
            params: {
              idcmd
            }
          });
        } else if (index === 3 && this.$route.name !== 'shoporders') {
          this.$router.push({
            name: 'shoporders'
          });
        } else if (index === 4 && this.$route.name !== 'shoplogout') {
          this.shopLogout();
        }
      } else if (index === 0 && this.$route.name !== 'shoplogin') {
        this.$router.push({
          name: 'shoplogin'
        });
      }
    },
    // if (index === 0 && this.$route.name !== 'shophome') {
    //   this.$router.push({name: 'shophome'});
    // } else if (index === 1 && this.$route.name !== 'bankhome') {
    //   this.$router.push({name: 'bankhome'});

    // { text: 'Boutique', color: 'white' },
    // { text: 'Banque', color: 'grey' }

    updateNavBar() {
      if (this.$store.state.shopUser !== null && this.$store.state.shopUser !== undefined) {
        this.titles = [{
          text: 'Home',
          color: 'white'
        }, {
          text: 'Acheter',
          color: 'blue'
        }, {
          text: 'Payer',
          color: 'red'
        }, {
          text: 'Mes commandes',
          color: 'pink'
        }, {
          text: 'Logout',
          color: 'green'
        }];
      } else {
        this.titles = [{
          text: 'Login',
          color: 'green'
        }];
        if (this.$route.name !== 'shoplogin') {
          this.$router.push({
            name: 'shoplogin'
          });
        }
      }
    },
    ...mapActions(['shopLogout'])
  }
};
