import { ref, computed } from 'vue';

// Utiliser defineProps pour déclarer les props

export default {
  __name: 'BankAmount',
  props: ['balance'],
  setup: function setup(__props) {
    var balance = __props.balance;

    // Utiliser une référence pour le solde
    var balanceRef = ref(balance);

    // Calculer le style en fonction du solde (rouge si négatif, vert si positif)
    var balanceStyle = computed(function () {
      return {
        color: balanceRef.value < 0 ? 'red' : 'green'
      };
    });

    // Formater le solde avec le symbole €
    var formattedBalance = computed(function () {
      return "".concat(balanceRef.value, " \u20AC");
    });
    return {
      __sfc: true,
      balanceRef: balanceRef,
      balanceStyle: balanceStyle,
      formattedBalance: formattedBalance
    };
  }
};
