import "core-js/modules/es.array.push.js";
import ItemsList from '../../src/components/ItemsList.vue';
import BasketList from '../../src/components/BasketList.vue';
import { mapActions } from 'vuex';
export default {
  components: {
    ItemsList,
    BasketList
  },
  mounted() {
    this.initPage();
  },
  data() {
    return {
      viruses: [],
      cartItems: [],
      checkedViruses: [],
      checkedCartItems: []
    };
  },
  methods: {
    ...mapActions(['getAllViruses', 'addToCartAction', 'removeFromCartAction', 'clearCartAction', 'checkoutAction']),
    async initPage() {
      await this.getAllViruses();
      this.viruses = this.$store.state.viruses;
      console.log(this.viruses);
    },
    addToCart({
      item,
      amount
    }) {
      if (!amount || isNaN(parseInt(amount))) {
        alert(`Veuillez sélectionner une quantité valide !\nQuantité actuelle : ${amount}`);
        return;
      }
      const selectedVirus = item; // Utilisez directement l'objet virus
      const existingItemIndex = this.cartItems.findIndex(cartItem => cartItem.virus._id === selectedVirus._id);
      if (existingItemIndex !== -1) {
        this.cartItems[existingItemIndex].quantity = parseInt(this.cartItems[existingItemIndex].quantity) + parseInt(amount);
      } else {
        this.cartItems.push({
          virus: selectedVirus,
          quantity: parseInt(amount)
        });
      }
      this.addToCartAction({
        virus: selectedVirus,
        quantity: parseInt(amount)
      });
    },
    addAllToCart(selectedItems) {
      console.log('ShopBuy');
      console.log(selectedItems);
      /*selectedItems.forEach(({ item, amount }) => {
        console.log(item, amount)
        this.addToCart({ item, amount });
      });*/
    },
    toggleCartItemCheckbox(index) {
      // Logique pour gérer la sélection/désélection des articles dans le panier
      this.checkedCartItems[index] = !this.checkedCartItems[index];
    },
    removeFromCart(index) {
      // Logique pour retirer un article du panier
      const selectedCartItem = this.cartItems[index];
      // Appel de l'action du store pour retirer du panier
      this.removeFromCartAction(selectedCartItem);
      // Retrait également du panier local de la page
      this.cartItems.splice(index, 1);
    },
    clearCart() {
      // Logique pour vider le panier
      this.cartItems = [];
      this.clearCartAction();
    },
    checkout() {
      // Logique pour finaliser l'achat
      this.checkoutAction(this.cartItems);
    },
    toggleVirusCheckbox(index) {
      const updatedCheckedViruses = [...this.checkedViruses];
      const indexInArray = updatedCheckedViruses.indexOf(index);
      if (indexInArray === -1) {
        updatedCheckedViruses.push(index);
      } else {
        updatedCheckedViruses.splice(indexInArray, 1);
      }
      this.checkedViruses = updatedCheckedViruses;
    }
  }
};
