// App.vue

<template>
  <div id="app">
    <NavBar :titles="titles" @menu-clicked="goTo($event)"></NavBar>

    <h1>Welcome to DrMad app</h1>

    <router-view />
  </div>
</template>

<script>
import NavBar from "@/components/NavBar";

export default {
  name: "App",
  components: { NavBar },

  data: () => ({
    titles: [],
    currentIndex: -1,
  }),

  mounted() {
    this.updateNavBar();
  },

  watch: {
    $route: "updateNavBarOnRouteChange",
  },

  methods: {
    goTo(index) {
      if (this.titles.length === 3) {
        if (index === 0 && this.$route.name !== '/shop/items') {
          this.$router.push('/shop/items');
        } else if (index === 1 && this.$route.name !== '/bank/account') {
          this.$router.push('/bank/account');
        } else if (index === 2 && this.$route.name !== 'shoplogout') {
          this.$router.push({ name: 'shoplogout' });
        }
      } else {
        if (index === 0 && this.$route.name !== 'shoplogin') {
          this.$router.push({ name: 'shoplogin' });
        }
      }
    },

    updateNavBar() {
      if (this.$store.state.shopUser !== null && this.$store.state.shopUser !== undefined) {
        this.titles = [
          { text: 'Virus', color: 'blue' },
          { text: 'Compte bancaire', color: 'red' },
          { text: 'Logout', color: 'green' }
        ];
        console.log("La NavBar à été mise à jours !");
      } else {
        this.titles = [{ text: 'Login', color: 'green' }];
        console.log("La NavBar à été mise à jours !");
      }
    },

    updateNavBarOnRouteChange() {
      this.updateNavBar();
    },
  },
};
</script>
