import "core-js/modules/es.array.push.js";
import NavBar from "@/components/NavBar";
import { mapActions } from "vuex";
export default {
  name: "App",
  components: {
    NavBar
  },
  data: () => ({
    titles: [],
    currentIndex: -1
  }),
  mounted() {
    this.updateNavBar();
  },
  watch: {
    $route: "updateNavBar"
  },
  methods: {
    goTo(index) {
      if (this.titles.length > 1) {
        if (this.$route.name === 'home') {
          if (index === 0 && this.$route.name !== 'shophome') {
            this.$router.push({
              name: 'shophome'
            });
          } else if (index === 1 && this.$route.name !== 'bankhome') {
            this.$router.push({
              name: 'bankhome'
            });
          }
        } else if (this.$route.name === 'shophome' || this.$route.name === 'shopbuy' || this.$route.name === 'shoppay' || this.$route.name === 'shoporders') {
          if (index === 0 && this.$route.name !== 'shophome') {
            this.$router.push({
              name: 'shophome'
            });
          } else if (index === 1 && this.$route.name !== 'shopbuy') {
            this.$router.push({
              name: 'shopbuy'
            });
          } else if (index === 2 && this.$route.name !== 'shoppay') {
            const idcmd = "0";
            this.$router.push({
              name: 'shoppay',
              params: {
                idcmd
              }
            });
          } else if (index === 3 && this.$route.name !== 'shoporders') {
            this.$router.push({
              name: 'shoporders'
            });
          } else if (index === 4 && this.$route.name !== 'shoplogout') {
            this.shopLogout();
          }
        } else if (this.$route.name === 'bankhome' || this.$route.name === 'bankaccount') {
          if (index === 0 && this.$route.name !== 'bankaccount') {
            this.$router.push({
              name: 'bankaccount'
            });
          }
        }
      } else if (index === 0 && this.$route.name !== 'shoplogin') {
        this.$router.push({
          name: 'shoplogin'
        });
      }
    },
    updateNavBar() {
      if (this.$store.state.shopUser !== null && this.$store.state.shopUser !== undefined) {
        console.log(this.$route.name);
        if (this.$route.name === 'home') {
          this.titles = [{
            text: 'Boutique',
            color: 'white'
          }, {
            text: 'Banque',
            color: 'white'
          }];
        } else if (this.$route.name === 'shophome' || this.$route.name === 'shopbuy' || this.$route.name === 'shoppay' || this.$route.name === 'shoporders') {
          this.titles = [{
            text: 'Home',
            color: 'white'
          }, {
            text: 'Acheter',
            color: 'white'
          }, {
            text: 'Payer',
            color: 'white'
          }, {
            text: 'Mes commandes',
            color: 'white'
          }, {
            text: 'Logout',
            color: 'white'
          }];
        } else if (this.$route.name === 'bankhome' || this.$route.name === 'bankaccount') {
          this.titles = [{
            text: 'Mon Compte',
            color: 'white'
          }];
        }
      } else {
        this.titles = [{
          text: 'Login',
          color: 'white'
        }];
        if (this.$route.name !== 'shoplogin') {
          this.$router.push({
            name: 'shoplogin'
          });
        }
      }
    },
    ...mapActions(['shopLogout'])
  }
};
